
function Controller(){
    var request, response;
}

Controller.prototype.Execute = function(entity, request, response){
    var store;
    this.request = request;
    this.response = response;
    
    this.Send({search_results_array : [
			{
				library_link: "library_link string",
				library_avatar: "library_avatar url string",
				library_name: "library_name string",
				description: "description string",
				author: {
					       name: "name string",
					       author_link: "link string"
				        },
				date: "date last update string",
				comments: 100500,
				likes: 100500
			}
		      ]});
}

Controller.prototype.Send = function(result){
    this.response.writeHead(200,{"Content-Type":"application/json"});
    this.response.write(JSON.stringify({controller: "search", entity: result}));
    this.response.end();
}

Controller.prototype.GetName = function(){
    return 'search';
}

module.exports = Controller;