
function Validator(){
    
}

Validator.prototype.Validate = function(entity){
    var result = {};
    if (entity.password == undefined || entity.login == undefined){
        return false;
    }
    
    result.publicInfo = {
                            profile_link: "profile link string",
                            name: "name string",
                            avatar: "avatar url string"
                        };
    result.privateInfo = '123';
    
    return result;
}

module.exports = Validator;